import React, { Component } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

export class App extends Component {

  constructor (props){
    super(props)
    this.state = {
      locationInfo: {}
    }
  }
  submitForm = async (event) => {
    event.preventDefault();
    const city = event.target.className.value;
    const response = await axios.get (`https://us1.locationiq.com/v1/search.php?key=${process.env.REACT_APP_KEY}&q=${city}&format=json`)

    this.setState({
      locationInfo: response.data[0],
    })

  }


  render() {
    return (
      <div>
        <Form onSubmit="submitForm">
          <Form.Group className="mb-3" controlId="formBasicEmail" value= "city">
            <Form.Label>City Name</Form.Label>
            <Form.Control type="email" placeholder="City Name" />
          </Form.Group>

          <Button variant="primary" type="submit">
            Explore!
          </Button>
        </Form>
        {
          this.state.locationInfo.display_name && 
        <p>
          <p>{this.state.locationInfo.display_name}</p>
          <p>{this.state.locationInfo.lon}</p>
          <p>{this.state.locationInfo.lat}</p>
        </p>
        }
      </div>
    )
  }
}

export default App
